import React, { useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

function EditSerwisant() {
  const { obszarId } = useParams();
  const [serwisant, setSerwisant] = useState({ id: '', nazwisko: '', email: '' });

  const handleSubmit = event => {
    event.preventDefault();
    axios.put(`/api/obszary/${obszarId}/serwisant`, serwisant)
      .then(() => alert('Serwisant updated successfully!'))
      .catch(error => console.error('Error updating serwisant', error));
  };

  return (
    <div className="container mt-3">
      <h1>Edit Technician</h1>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label htmlFor="nazwisko" className="form-label">Surname</label>
          <input
            type="text"
            className="form-control"
            id="nazwisko"
            value={serwisant.nazwisko}
            onChange={e => setSerwisant({ ...serwisant, nazwisko: e.target.value })}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="email" className="form-label">Email</label>
          <input
            type="email"
            className="form-control"
            id="email"
            value={serwisant.email}
            onChange={e => setSerwisant({ ...serwisant, email: e.target.value })}
          />
        </div>
        <button type="submit" className="btn btn-primary">Submit</button>
      </form>
    </div>
  );
}

export default EditSerwisant;
