import React, { useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

function AddDzialanie() {
  const { obszarId } = useParams();
  const [dzialanie, setDzialanie] = useState({ opis: '', czas: '' });

  const handleSubmit = event => {
    event.preventDefault();
    axios.post(`api/dzialania/${obszarId}/dzialanie`, dzialanie)
      .then(() => alert('Action added successfully!'))
      .catch(error => console.error('Error adding action', error));
  };

  return (
    <div className="container mt-3">
      <h1>Add Action</h1>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label htmlFor="opis" className="form-label">Description</label>
          <input
            type="text"
            className="form-control"
            id="opis"
            value={dzialanie.opis}
            onChange={e => setDzialanie({ ...dzialanie, opis: e.target.value })}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="czas" className="form-label">Planned Time</label>
          <input
            type="number"
            className="form-control"
            id="czas"
            value={dzialanie.czas}
            onChange={e => setDzialanie({ ...dzialanie, czas: e.target.value })}
          />
        </div>
        <button type="submit" className="btn btn-primary">Add</button>
      </form>
    </div>
  );
}

export default AddDzialanie;
